class PaintBoard
{
    // private string[,] boardTmplCurr = (string[,])
    privatePos 


    // This method places the pawn on the board
    private void PlacePawn(Pawn pawn, Tuple<int, int> position, int offset)
    {
        int commonPos = position.Item1;
        int privatePos = position.Item2;
        string colour = pawn.Colour.ToLower();

        Tuple<int, int> boardPos = null;

        if (privatePos > 0)
        {
            boardPos = CODE_COLOUR_SQUARES[colour][privatePos];
        }
        else if (commonPos == 0)
        {
            boardPos = CODE_POOL_PLACES[colour][pawn.Index];
            offset = 0; // No need for offset in pool positions
        }
        else
        {
            boardPos = CODE_COMMON_SQUARES[commonPos];
        }

        int row = boardPos.Item1;
        int column = boardPos.Item2;

        if (offset > 0)
        {
            boardTmplCurr[row - 1, column + offset] = pawn.Id[1];
        }
        else
        {
            boardTmplCurr[row - 1, column - 1] = pawn.Id[0];
            boardTmplCurr[row - 1, column] = pawn.Id[1];
        }
    }

    // This method places all pawns based on their positions
    private void PlacePawns(Dictionary<Tuple<int, int>, List<Pawn>> positionPawns)
    {
        foreach (var position in positionPawns)
        {
            foreach (var (index, pawn) in position.Value.Select((pawn, index) => (index, pawn)))
            {
                PlacePawn(pawn, position.Key, index);
            }
        }
    }

    // This method paints the board
    public string Paint(Dictionary<Tuple<int, int>, List<Pawn>> position)
    {
        boardTmplCurr = new int[20, 20];  // Reset the board
        PlacePawns(position);

        var boardPaint = new List<string>();

        for (int i = 0; i < boardTmplCurr.GetLength(0); i++)
        {
            boardPaint.Add(string.Join("", Enumerable.Range(0, boardTmplCurr.GetLength(1)).Select(j => boardTmplCurr[i, j].ToString())));
        }

        return string.Join(Environment.NewLine, boardPaint);
    }
}

class Pawn
{
    public string Colour { get; set; }
    public int Index { get; set; }
    public string Id { get; set; }
}

class Program
{
    static void Main(string[] args)
    {
        // Example usage (for testing purposes)
        var paintBoard = new PaintBoard();
        var positionPawns = new Dictionary<Tuple<int, int>, List<Pawn>>();
        var pawns = new List<Pawn> { new Pawn { Colour = "yellow", Index = 1, Id = "XY" } };
        positionPawns.Add(new Tuple<int, int>(1, 1), pawns);

        Console.WriteLine(paintBoard.Paint(positionPawns));
    }
}
